import React, { useState, useEffect, useMemo } from 'react';
import Table from './components/Table';
import axios from 'axios';

function App() {
	const [ data, setData ] = useState([]);

	// Using useEffect to call the API once mounted and set the data
	useEffect(() => {
		(async () => {
			const result = await axios('https://randomuser.me/api/?results=15');
			setData(result.data);
		})();
	}, []);
	const columns = useMemo(() => [

		{
			Header: "Employee",

			columns: [

		{
			Header: 'Image',
			accessor: 'results.picture.thumbnail'
		},
		{
			Header: 'Name',
			accessor: [ 'results.name.last', 'results.name.first' ]
		},
	],
},
		{ 
			Header: "Details",

		columns: [
			{
			Header: 'Age',
			accessor: 'results.dob.age'
		},
		{
			Header: 'Contact',
			accessor: 'results.email'
		},
		{
			Header: 'Location',
			accessor: 'results.location.city'
		}
	]
}
],
[]
);
	return (
		<div className="App">
			<Table columns={columns} data={data} />
		</div>
	);
}

